import "./styles.css";
import {
  createBrowserRouter,
  Navigate,
  RouterProvider,
} from "react-router-dom";

import { Navbar } from "./components/Navbar";
import { Home } from "./pages/Home";
import { List } from "./pages/List";
import { Contact } from "./pages/Contact";
import { ItemDetails } from "./pages/ItemDetails";
import { NotFound } from "./pages/NotFound";
import { useState } from "react";

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Custom route guard component
  function ProtectedRoute({ element, guardCondition }) {
    return guardCondition ? element : <Navigate to="/" />;
  }

  const router = createBrowserRouter([
    {
      path: "/",
      errorElement: <NotFound />,
      element: <Navbar />,
      children: [
        {
          index: true,
          element: (
            <ProtectedRoute
              element={
                <Home loggedIn={isLoggedIn} setLoggedIn={setIsLoggedIn} />
              }
              guardCondition={isLoggedIn} // Allow access only if logged in
            />
          ),
        },
        {
          path: "/contact",
          element: (
            <ProtectedRoute
              element={<Contact />}
              guardCondition={isLoggedIn} // Allow access only if logged in
            />
          ),
        },
        {
          path: "/list",
          children: [
            {
              index: true,
              element: (
                <ProtectedRoute
                  element={<List />}
                  guardCondition={isLoggedIn} // Allow access only if logged in
                />
              ),
            },
            {
              path: ":itemId",
              element: (
                <ProtectedRoute
                  element={<ItemDetails />}
                  guardCondition={isLoggedIn} // Allow access only if logged in
                />
              ),
            },
          ],
        },
      ],
    },
  ]);

  return (
    <div className="App">
      <RouterProvider router={router} />
    </div>
  );
}
