import "./styles.css";
import {
  createBrowserRouter,
  Navigate,
  RouterProvider
} from "react-router-dom";

import { Navbar } from "./components/Navbar";
import { Home } from "./pages/Home";
import { List } from "./pages/List";
import { Contact } from "./pages/Contact";
import { ItemDetails } from "./pages/ItemDetails";
import { NotFound } from "./pages/NotFound";
import { useState } from "react";

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  // create high-level protected route below
  function ProtectedRoute({ element, isLoggedIn, redirectPath }) {
    return isLoggedIn ? element : <Navigate to={redirectPath} replace={true} />;
  }
  // protect the routes for the contact, list and item details pages
  const router = createBrowserRouter([
    {
      path: "/",
      errorElement: <NotFound />,
      element: <Navbar />,
      children: [
        {
          index: true,
          element:  <ProtectedRoute
              element={<Home loggedIn={isLoggedIn} setLoggedin={setIsLoggedIn} />}
              isLoggedIn={isLoggedIn}
              redirectPath="/home"
            />
        },
        {
          path: "/contact",
          element: <Contact />
        },
        {
          path: "/list",
          children: [
            {
              index: true,
              element: <List />
            },
            {
              path: ":itemId",
              element: <ItemDetails />
            }
          ]
        }
      ]
    }
  ]);

  return (
    <div className="App">
      <RouterProvider router={router} />
    </div>
  );
}
