import "./styles.css";
import {
  createBrowserRouter,
  Navigate,
  Route,
  RouterProvider
} from "react-router-dom";

import { Navbar } from "./components/Navbar";
import { Home } from "./pages/Home";
import { List } from "./pages/List";
import { Contact } from "./pages/Contact";
import { ItemDetails } from "./pages/ItemDetails";
import { NotFound } from "./pages/NotFound";
import { useState } from "react";

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Custom route guard component
  function ProtectedRoute({ element, redirectTo, guardCondition }) {
    return guardCondition ? element : <Navigate to={redirectTo} />;
  }

  const router = createBrowserRouter([
    {
      path: "/",
      errorElement: <NotFound />,
      element: <Navbar />,
      children: [
        {
          index: true,
          element: (
            <ProtectedRoute
              element={<Home loggedIn={isLoggedIn} setLoggedIn={setIsLoggedIn} />}
              redirectTo="/home"
              guardCondition={!isLoggedIn} // Redirect if not logged in
            />
          )
        },
        {
          path: "/contact",
          element: <Contact />
        },
        {
          path: "/list",
          children: [
            {
              index: true,
              element: <List />
            },
            {
              path: ":itemId",
              element: <ItemDetails />
            }
          ]
        }
      ]
    },
    {
      path: "/home",
      element: <Home loggedIn={isLoggedIn} setLoggedIn={setIsLoggedIn} />
    }
  ]);

  return (
    <div className="App">
      <RouterProvider router={router} />
    </div>
  );
}
