import "./styles.css";
import {
  createBrowserRouter,
  Navigate,
  RouterProvider
} from "react-router-dom";

import { Navbar } from "./components/Navbar";
import { Home } from "./pages/Home";
import { List } from "./pages/List";
import { Contact } from "./pages/Contact";
import { ItemDetails } from "./pages/ItemDetails";
import { NotFound } from "./pages/NotFound";
import { useState } from "react";

// Custom route guard component
function ProtectedRoute({ element, isLoggedIn, redirectPath }) {
  return isLoggedIn ? element : <Navigate to={redirectPath} replace={true} />;
}

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const router = createBrowserRouter([
    {
      path: "/",
      errorElement: <NotFound />,
      element: <Navbar />,
      children: [
        {
          index: true,
          element: (
            <ProtectedRoute
              element={<Home loggedIn={isLoggedIn} setLoggedin={setIsLoggedIn} />}
              isLoggedIn={isLoggedIn}
              redirectPath="/home"
            />
          )
        },
        {
          path: "/contact",
          element: <Contact />
        },
        {
          path: "/list",
          children: [
            {
              index: true,
              element: <List />
            },
            {
              path: ":itemId",
              element: <ItemDetails />
            }
          ]
        }
      ]
    },
    {
      path: "/home",
      element: <Home loggedIn={isLoggedIn} setLoggedin={setIsLoggedIn} />
    }
  ]);

  return (
    <div className="App">
      <RouterProvider router={router} />
    </div>
  );
}
